# Show only in dock and remove counter
defaults write com.todoist.mac.Todoist ShowInDock -bool true
defaults write com.todoist.mac.Todoist ShowBadgeInDock -bool false
defaults write com.todoist.mac.Todoist ShowTodoistMenuBar -bool false

# Disable notifications
defaults write com.todoist.mac.Todoist NotifyViaDesktopNotifications -bool false
defaults write com.todoist.mac.Todoist RemindViaDesktopNotifications -bool false

# Set quick add shortcut to ⌃+⌥+A (ctrl+option+A)
defaults write com.todoist.mac.Todoist TDQuickAddShortcut -data ""

# Remove show/hide shortcut
defaults write com.todoist.mac.Todoist TDToggleShortcut -data "62706c6973743030d40102030405060809582476657273696f6e58246f626a65637473592461726368697665725424746f7012000186a0a10755246e756c6c5f100f4e534b657965644172636869766572d10a0b54726f6f74800008111a232d3237393f5154590000000000000101000000000000000c0000000000000000000000000000005b"
